function countRightAnswers(e){for(var t=0,r=0;r<e.length;r++){var n=e[r].getAttribute("isright");"true"==n?(t++,e[r].parentNode.classList.add("answers__btn--right")):"false"==n&&e[r].parentNode.classList.add("answers__btn--wrong")}return t}function countWrongAnswers(e){return answersCount-e}function animateProgressBar(e,t){var r=countPercent(e,answersCount);progressBar.style.width=r+"%",errorsCount.innerHTML=t;var n=0,s=500/r,o=setInterval(function(){n==r&&clearInterval(o),progressPercent.innerHTML=n+"%",n++},s)}function countPercent(e,t){var r=e/t*100;return r=Math.round(r)}var submit=document.getElementById("submitJs"),cross=document.getElementById("crossJs"),result=document.getElementById("resultJs"),answersCount=document.getElementsByClassName("question").length;submit.onclick=function(){var e=document.querySelectorAll(".answers__input:checked"),t=countRightAnswers(e),r=countWrongAnswers(t);result.classList.remove("result--hidden"),setTimeout(function(){animateProgressBar(t,r)},1e3)},cross.onclick=function(){result.classList.add("result--hidden")};var progressPercent=document.getElementById("progressPercentJs"),progressBar=document.getElementById("progressBarJs"),errorsCount=document.getElementById("errorsCountJs");